  return (
    <div className="App">
      <header>
        <div id="hamburger-menu">
          <input id="hamburger--toggle" className="hamburger" type="checkbox"
            checked={menuOpen}
            readOnly
            onClick={() => {
              menuOpen? toggleMenu(false): toggleMenu(true);
