import logo from './logo.svg';
import {useState} from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import "./App.scss";

const menu = ["home", "education", "portfolio", "contact"]

const App = () => {
  const [page,loadPage] = useState();
  const [menuOpen, toggleMenu] = useState(false);

  const navigate = useNavigate();
  return (
    <div className="App">
      <header>
        <div id="hamburger-menu">
          <input id="hamburger--toggle" className="hamburger" type="checkbox"
            checked={menuOpen}
            readOnly
            onClick={() => {
              menuOpen? toggleMenu(false): toggleMenu(true);
            }}
          />
          <label htmlFor="hamburger--toggle">
            <div>
              <div className="hamburger layers" />
              <div className="hamburger layers" />
              <div className="hamburger layers" />
            </div>
          </label>
          <nav id="menu">
            <ul>
              { menu.map((item) => (
                <li key = { item } >
                  <input
                    className="menu"
                    name = "menu"
                    type = "radio"
                    value = { item }
                    id = { item }
                    checked = { page === item }
                    onChange = {() => {
                      loadPage(item);
                      (item === "introduction") ?
                        navigate("")
                       :
                        navigate("/" + item);
                    }}
                    />
                    <label htmlFor = { item }>
                      { item }
                    </label>
                  </li>
                ))}
            </ul>
          </nav>
        </div>
        <div id="logo">Rohit Naidu</div>
      </header>
      <Routes>
      <Route path ="*" element = { <Landing /> }
        key = { document.location.href }
      />
      </Routes>
    </div>
  );
}

export default App;
