import { useState, useEffect, useMemo, useCallback } from "react";
import debounce from "lodash.debounce";

import "./App.scss";

//components
import Hamburger from "./Components/Hamburger/Hamburger";
import Menu from "./Components/Menu/Menu";

//pages
import Home from "./Pages/Home/index";
import Education from "./Pages/Education/index";
import Portfolio from "./Pages/Portfolio/index";
import Contact from "./Pages/Contact/index";

const App = () => {

  const [view, setView] = useState(0);
  const [menuState, toggleMenu] = useState(false);

  const menu = ["home", "education", "portfolio", "contact"];

  const _handleScroll = () => {
    toggleMenu(false);
    updateView(window.pageYOffset);
  }

  const updateView = offset => {
    console.log("last scroll: " + offset);
    console.log("current scroll: " + window.pageYOffset)
    setView((view + 1 < menu.length-1)  ? view+1: view.length-1);
    if (offset < window.pageYOffset) {
      //scroll down
      console.log("scroll down");
    } else if (offset > window.pageYOffset) {
      //scroll up
      console.log("scroll up")
    }

  }

  useEffect(() => {
    console.log(`view value = ${view}`)
    console.log(`scrolling to article#${menu[view]}`)
    document.querySelector(`article#${menu[view]}`).scrollIntoView({behavior:"smooth"})
    setTimeout(() => {
      window.addEventListener("scroll", _handleScroll, {once: true});
    }, 500)
  }, [view])

  return (
    <div className="App">
      <header>
        <Hamburger menuState={menuState} toggleMenu={toggleMenu} />
      </header>
      <Menu menu={menu} menuState={menuState} toggleMenu={toggleMenu} view={view} setView={setView} />
      <div id="container">
        <div id="background" />
        <Home />
        <div id="article-wrapper">
          <Education />
          <Portfolio />
          <Contact />
        </div>
      </div>
    </div>
  );
}

export default App;
