import {useState, useEffect} from "react";
import { Routes, Route, useNavigate } from "react-router-dom";

import Hamburger from "./Components/Hamburger/Hamburger";
import Home from "./Components/Home/index";
import Education from "./Components/Education/index";
import Portfolio from "./Components/Portfolio/index";
import Contact from "./Components/Contact/index";
import "./App.scss";

const menu = ["home", "education", "portfolio", "contact"];

const App = () => {
  const navigate = useNavigate();
  const [menuState, toggleMenu] = useState(false);

  const handleClick = e => {
    toggleMenu(false);
  }
  const handleScroll = e => {
    toggleMenu(false);
    console.log("scrolling");
  }

  useEffect(() => {
    const container = document.getElementById("container");
    container.addEventListener("click", handleClick);
    window.addEventListener("scrollDown", handleScroll);

    return() => {
      container.removeEventListener("click", handleClick);
      window.addEventListener("scroll", handleScroll);
    }
  },[])

  return (
    <div className="App">
      <header>
        <Hamburger menuState={menuState} toggleMenu={toggleMenu} />
      </header>
      <nav id="menu" className={menuState? "":"slideOut"}>
        <ul>
          { menu.map((item) => (
            <label htmlFor = { item } key = { item } >
              <li>
                <input
                  className="menu"
                  name = "menu"
                  type = "radio"
                  value = { item }
                  id = { item }
                  checked = { item.index === item }
                  onChange = {() => {
                    item === "introduction" ?
                      navigate("")
                    : navigate("/" + item);
                    menuState? toggleMenu(false): toggleMenu(true);
                  }}
                /> { item.at(0).toUpperCase()+item.slice(1,item.length) }
              </li>
            </label>
            ))}
        </ul>
      </nav>
      <div id="container">
        <div id="background">
        </div>
        <div id="article-wrapper">
          <div id="bio">
              <div id="info">
                <div className="title">Rohit Naidu</div>
                <div id="socials">
                  <a href="https://github.com/rohtett/" target="_blank">
                    <div className="github" />
                  </a>
                  <a href="https://www.linkedin.com/in/rohtett/" target="_blank">
                    <div className="linkedin" />
                  </a>
                </div>
              </div>
          </div>

        </div>
      </div>
    </div>
  );
}

const Landing = () => {
  return (
    <article>
      <h3>Error 404: Page not Found</h3>
      <p> Please use the navigation bar to locate to another page.</p>
    </article>
  )
}


export default App;
