import "./Menu.scss";

const Menu = ({toggleMenu, menuState, view, setView}) => {

  const menu = ["home", "education", "portfolio", "contact"];

  return (
    <nav id="menu" className={menuState? "":"slideOut"}>
      <ul onClick={() => {
        toggleMenu(false);
      }}>
        { menu.map((item) => (
          <li>
            <input type = "radio" readOnly
              className="menu" name = "menu"
              value = { item } id = { item }
              checked = { view === item }
            />
            <label htmlFor = { item } key = { item }
              onClick = {() => {
                  document.querySelector(`article#${item}`).scrollIntoView({behavior:"smooth"})
              }
            }>
              {item.at(0).toUpperCase()+item.slice(1,item.length) }
            </label>
          </li>
          ))}
      </ul>
    </nav>
  )
}

export default Menu;
